// src/components/MainLayout.js
import React from 'react';
import { Outlet, useNavigate, NavLink } from 'react-router-dom';
import './MainLayout.css';

const navItems = [
  { path: '/app/home', icon: 'fa-solid fa-house', label: 'Ìôà' },
  { path: '/app/likes', icon: 'fa-solid fa-heart', label: 'ÌïòÌä∏' },
  { path: '/app/match', icon: 'fa-solid fa-compass', label: 'Îß§Ïπò' },
  { path: '/app/chat', icon: 'fa-solid fa-comment', label: 'Ï±ÑÌåÖ' },
];

function MainLayout() {
  const navigate = useNavigate();
  const handleLogout = () => {
    // Ïã§Ï†ú Î°úÍ∑∏ÏïÑÏõÉ Î°úÏßÅ (Ïòà: localStorage.removeItem('token');)
    // ÌòÑÏû¨Îäî ÏÑ∏ÏÖò Í¥ÄÎ¶¨Í∞Ä ÏóÜÏúºÎØÄÎ°ú, Îã®ÏàúÌûà Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Î¶¨ÎîîÎ†âÏÖò
    navigate('/login');
  };

  return (
    <div className="main-layout">
      <header className="main-header"> {/* ÏÉà Ìó§Îçî Ï∂îÍ∞Ä */}
        <h1 className="main-header-title">Mungeting üêæ</h1> {/* Ïï± Ïù¥Î¶Ñ ÎòêÎäî Î°úÍ≥† */}
        <button onClick={handleLogout} className="logout-button">
          Î°úÍ∑∏ÏïÑÏõÉ
        </button>
      </header>
      <main className="main-content">
        <Outlet />
      </main>
      <nav className="bottom-nav">
        {navItems.map((item) => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}>
            <i className={item.icon}></i>
            <span>{item.label}</span>
          </NavLink>
        ))}
      </nav>
    </div>
  );
}

export default MainLayout;
