// src/components/DogInfoForm.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Input from './Input';
import Select from './Select';
import './UserInfoForm.css'; // 스타일 재활용

const dogBreedOptions = [
  { value: 'golden_retriever', label: '골든 리트리버' },
  { value: 'shiba_inu', label: '시바견' },
  { value: 'poodle', label: '푸들' },
  { value: 'maltese', label: '말티즈' },
  { value: 'chihuahua', label: '치와와' },
  { value: 'etc', label: '기타' },
];

const dogGenderOptions = [
  { value: 'male', label: '수컷' },
  { value: 'female', label: '암컷' },
];

function DogInfoForm() {
  const [formData, setFormData] = useState({
    breed: '',
    gender: '',
    age: '',
    introduction: '',
  });
  const navigate = useNavigate();
  const location = useLocation();
  const userInfo = location.state?.userInfo;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const finalData = { ...userInfo, dogInfo: formData };
    console.log('최종 회원가입 데이터:', finalData);
    navigate('/signup-success');
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="form">
        <h2 className="title">강아지 정보 입력</h2>
        <Select
          label="견종"
          name="breed"
          value={formData.breed}
          onChange={handleChange}
          options={dogBreedOptions}
        />
        <Select
          label="성별"
          name="gender"
          value={formData.gender}
          onChange={handleChange}
          options={dogGenderOptions}
        />
        <Input
          label="나이"
          type="number"
          name="age"
          value={formData.age}
          onChange={handleChange}
          required
        />
        <Input
          label="자기소개"
          type="textarea"
          name="introduction"
          value={formData.introduction}
          onChange={handleChange}
          required
        />
        <button type="submit" className="button">회원가입 완료</button>
      </form>
    </div>
  );
}

export default DogInfoForm;
