// src/components/HomePage.js
import React, { useState, useEffect } from 'react';
import './HomePage.css'; // 새로 만든 CSS 파일을 import

// API 호출을 위한 가짜 데이터 (실제로는 API로 받아와야 함)
const fakeMyDog = {
  name: '몽실이',
  breed: '비숑 프리제',
  age: 3,
  imageUrl: '/images/image.png', // public 폴더에 있는 이미지 경로
};

const fakeRankedDogs = [
  { rank: 1, name: '코코', breed: '푸들', age: 2, imageUrl: '/images/image.png' },
  { rank: 2, name: '보리', breed: '골든 리트리버', age: 4, imageUrl: '/images/image.png' },
  { rank: 3, name: '초코', breed: '치와와', age: 5, imageUrl: '/images/image.png' },
];

function HomePage() {
  const [myDog, setMyDog] = useState(null);
  const [rankedDogs, setRankedDogs] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Promise.all을 사용해 여러 API를 동시에 호출합니다.
        // 실제 API 엔드포인트로 수정해야 합니다.
        // const [myDogResponse, rankingResponse] = await Promise.all([
        //   fetch('http://192.168.0.15:8080/api/dogs/mydog'),
        //   fetch('http://192.168.0.15:8080/api/dogs/ranking?top=3'),
        // ]);

        // if (!myDogResponse.ok || !rankingResponse.ok) {
        //   throw new Error('데이터를 불러오는 데 실패했습니다.');
        // }

        // const myDogData = await myDogResponse.json();
        // const rankingData = await rankingResponse.json();

        // setMyDog(myDogData);
        // setRankedDogs(rankingData);

        // --- 임시로 가짜 데이터 사용 ---
        setMyDog(fakeMyDog);
        setRankedDogs(fakeRankedDogs);
        // ---------------------------

      } catch (err) {
        setError(err.message);
        console.error('Fetch error:', err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    return <div className="home-container"><p>오류: {error}</p></div>;
  }

  if (!myDog || rankedDogs.length === 0) {
    return <div className="home-container"><p>데이터를 불러오는 중입니다...</p></div>;
  }

  return (
    <div className="home-container">
      {/* 내 강아지 프로필 섹션 */}
      <section className="my-dog-section">
        <h2 className="section-title">내 강아지</h2>
        <div className="my-dog-card">
          <img src={myDog.imageUrl} alt={`${myDog.name} 프로필 사진`} />
          <h3 className="dog-name">{myDog.name}</h3>
          <p className="dog-details">{myDog.breed} / {myDog.age}살</p>
        </div>
      </section>


    </div>
  );
}

export default HomePage;
