// src/components/UserInfoForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Input from './Input';
import Select from './Select';
import './FormLayout.css';

const regionOptions = [
    { value: 'seoul', label: '서울' },
    { value: 'busan', label: '부산' },
    { value: 'incheon', label: '인천' },
    { value: 'gwangju', label: '광주' },
    { value: 'daegu', label: '대구' },
    { value: 'daejeon', label: '대전' },
    { value: 'sejong', label: '세종' },
    { value: 'ulsan', label: '울산' },
    { value: 'gyeonggi', label: '경기' },
];

const genderOptions = [
    { value: 'male', label: '남성' },
    { value: 'female', label: '여성' },
];

function UserInfoForm() {
    const [formData, setFormData] = useState({
        nickname: '',
        id: '',
        password: '',
        passwordConfirm: '',
        region: '',
        gender: '',
    });
    // error 상태를 객체로 변경
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
        // 입력이 변경될 때 해당 필드의 에러 메시지 제거
        setErrors((prev) => ({ ...prev, [name]: '' }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let newErrors = {};
        let isValid = true;

        if (formData.password !== formData.passwordConfirm) {
            newErrors.passwordConfirm = '비밀번호가 일치하지 않습니다.';
            isValid = false;
        }

        // 필드 유효성 검사
        if (!formData.nickname) newErrors.nickname = '별명을 입력해주세요.';
        if (!formData.id) newErrors.id = 'ID를 입력해주세요.';
        if (!formData.password) newErrors.password = '비밀번호를 입력해주세요.';
        if (!formData.region) newErrors.region = '사는곳을 선택해주세요.';
        if (!formData.gender) newErrors.gender = '성별을 선택해주세요.';

        if (Object.keys(newErrors).length > 0 || !isValid) {
            setErrors(newErrors);
            return;
        }

        // API 명세에 맞게 데이터 재구성
        const selectedRegion = regionOptions.find(option => option.value === formData.region);
        const selectedGender = genderOptions.find(option => option.value === formData.gender);

        const apiData = {
            nickname: formData.nickname,
            password: formData.password,
            confirmPassword: formData.passwordConfirm,
            gender: selectedGender ? selectedGender.label : '',
            city: selectedRegion ? selectedRegion.label : '',
            // district는 폼에 없으므로, 필요하다면 추가해야 합니다.
            // district: '강남구', 
        };

        try {
            const response = await fetch('http://192.168.0.15:8080/api/users/signup', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(apiData),
            });

            if (response.ok) {
                console.log('회원 정보 저장 성공');
                // 성공 시 다음 단계로 사용자 정보와 함께 이동
                // 강아지 정보 등록 시 userId가 필요할 수 있으므로, API 응답에 userId가 있다면 전달하는 것이 좋습니다.
                navigate('/signup-dog', { state: { userInfo: apiData } });
            } else {
                const errorData = await response.json().catch(() => ({ message: '알 수 없는 오류가 발생했습니다.' }));
                alert(`회원가입 실패: ${errorData.message || '잘못된 요청입니다.'}`);
                console.error('회원가입 실패:', errorData);
            }
        } catch (error) {
            console.error('회원가입 API 호출 중 오류 발생:', error);
            alert('회원가입 중 문제가 발생했습니다. 잠시 후 다시 시도해주세요.');
        }
    };

    return (
        <div className="form-layout-container">
            <header className="header">
                <button className="back-button" onClick={() => navigate('/login')}>
                    <i className="fa-solid fa-arrow-left"></i>
                </button>
                <h1 className="header-title">회원 정보 입력</h1>
                <div style={{ width: '2rem' }}></div>
            </header>
            <main className="content">
                <form onSubmit={handleSubmit} className="form-content">
                    <Input label="별명" type="text" name="nickname" value={formData.nickname} onChange={handleChange} required error={errors.nickname} />
                    <Input label="ID" type="text" name="id" value={formData.id} onChange={handleChange} required error={errors.id} />
                    <Input label="PW" type="password" name="password" value={formData.password} onChange={handleChange} required error={errors.password} />
                    <Input label="PW 확인" type="password" name="passwordConfirm" value={formData.passwordConfirm} onChange={handleChange} required error={errors.passwordConfirm} />
                    <Select label="사는곳" name="region" value={formData.region} onChange={handleChange} options={regionOptions} error={errors.region} />
                    <Select label="성별" name="gender" value={formData.gender} onChange={handleChange} options={genderOptions} error={errors.gender} />
                    <div className="bottom-action">
                        <button type="submit" className="submit-button">다음</button>
                    </div>
                </form>
            </main>
        </div>
    );
}

export default UserInfoForm;
