// src/components/SignupForm.js

import React, { useState } from 'react';
import Input from './Input';
import Select from './Select';

const locationOptions = [
  { value: 'seoul', label: '서울' },
  { value: 'busan', label: '부산' },
  { value: 'incheon', label: '인천' },
];

const genderOptions = [
  { value: 'male', label: '남성' },
  { value: 'female', label: '여성' },
];

function SignupForm() {
  // 1. 상태(state)를 객체 하나로 관리하여 코드를 더 간결하게 만듭니다.
  const [formData, setFormData] = useState({
    name: '',
    id: '',
    pw: '',
    pwConfirm: '',
    location: '',
    gender: '',
  });
  const [error, setError] = useState('');

  // 2. 입력 값이 바뀔 때 상태를 업데이트하는 통합 핸들러
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // 3. 회원가입 제출 핸들러
  const handleSubmit = (e) => {
    e.preventDefault();

    // 객체 비구조화 할당으로 값에 쉽게 접근
    const { name, id, pw, pwConfirm, location, gender } = formData;

    if (pw !== pwConfirm) {
      setError('비밀번호가 일치하지 않습니다.');
      return;
    }
    setError('');

    console.log('회원가입 데이터:', { name, id, pw, location, gender });
    alert('회원가입 성공! (콘솔을 확인해보세요)');
  };

  return (
    <form onSubmit={handleSubmit}>
      <Input
        label="이름"
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        required
      />
      <Input
        label="ID"
        type="text"
        name="id"
        value={formData.id}
        onChange={handleChange}
        required
      />
      <Input
        label="PW"
        type="password"
        name="pw"
        value={formData.pw}
        onChange={handleChange}
        required
      />
      <Input
        label="PW 확인"
        type="password"
        name="pwConfirm"
        value={formData.pwConfirm}
        onChange={handleChange}
        required
      />
      <Select
        label="사는곳"
        name="location"
        value={formData.location}
        onChange={handleChange}
        options={locationOptions}
      />
      <Select
        label="성별"
        name="gender"
        value={formData.gender}
        onChange={handleChange}
        options={genderOptions}
      />

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <button type="submit">회원가입</button>
    </form>
  );
}

export default SignupForm;
